static bool dilithium_signature_compare(const uint8_t* seed, const uint8_t* kmsg, size_t kmsglen, const uint8_t* ksig, size_t ksiglen)
{
	uint8_t* mout = (uint8_t*)malloc(ksiglen);
	uint8_t* pk = (uint8_t*)malloc(QSC_DILITHIUM_PUBLICKEY_SIZE);
	uint8_t* sig = (uint8_t*)malloc(ksiglen);
	uint8_t* sk = (uint8_t*)malloc(QSC_DILITHIUM_PRIVATEKEY_SIZE);
	size_t msglen;
	size_t siglen;
	bool ret;

	assert(mout != (uint8_t*)0 && pk != (uint8_t*)0 && sig != (uint8_t*)0 && sk != (uint8_t*)0);

	qsc_intutils_clear8(mout, ksiglen);
	qsc_intutils_clear8(pk, QSC_DILITHIUM_PUBLICKEY_SIZE);
	qsc_intutils_clear8(sig, ksiglen);
	qsc_intutils_clear8(sk, QSC_DILITHIUM_PRIVATEKEY_SIZE);

	msglen = 0;
	siglen = 0;
	ret = true;
	qsctest_nistrng_prng_initialize(seed, NULL, 0);

	/* Vector 0 */
	/* generate public and secret seeds */
	qsc_dilithium_generate_keypair(pk, sk, qsctest_nistrng_prng_generate);

	/* sign the message */
	qsc_dilithium_sign(sig, &siglen, kmsg, kmsglen, sk, qsctest_nistrng_prng_generate);

	/* compare the signature cipher-text to the expected output */
	if (qsc_intutils_are_equal8(sig, ksig, ksiglen) != true)
	{
		qsctest_print_safe("Failure! dilithium_signature_compare: output does not match the known answer -DC1 \n");
		ret = false;
	}

	/* verify the message using the public seed */
	if (qsc_dilithium_verify(mout, &msglen, sig, siglen, pk) != true)
	{
		qsctest_print_safe("Failure! dilithium_signature_compare: signature verification check failure -DC2 \n");
		ret = false;
	}

	/* compare the two messages for equality */
	if (qsc_intutils_are_equal8(kmsg, mout, kmsglen) != true)
	{
		qsctest_print_safe("Failure! dilithium_signature_compare: message does not equal to the original -DC3 \n");
		ret = false;
	}

	if (mout != NULL)
	{
		free(mout);
	}

	if (pk != NULL)
	{
		free(pk);
	}

	if (sig != NULL)
	{
		free(sig);
	}

	if (sk != NULL)
	{
		free(sk);
	}

	return ret;
}

